        self.cpu64 = 'data:image/png;base64,'
        self.camera64 = 'data:image/png;base64,'
        self.checkmark64 = 'data:image/png;base64,'
        self.cookbook64 = 'data:image/png;base64,'
        self.download64 = 'data:image/png;base64,'
        self.github64 = 'data:image/png;base64,'
        self.run64 = 'data:image/png;base64,'
